    public double energy(IAtomList atoms) {
        IAtomOriented atom0 = (IAtomOriented)atoms.getAtom(0);
        IAtomOriented atom1 = (IAtomOriented)atoms.getAtom(1);
        double rCM = Math.sqrt(atom0.getPosition().Mv1Squared(atom1.getPosition()));        
        double th1 = 0;
        double th2 = 0;
        double phi = 0;
        Vector ex = space.makeVector();
        Vector ey = space.makeVector();
        Vector ez = space.makeVector();
        Vector dr = space.makeVector();
        Vector a0 = space.makeVector();
        Vector a1 = space.makeVector();
        
        ex.E(0);
        ex.setX(0, 1);
        ey.E(0);
        ey.setX(1, 1);
        ez.E(0);
        ez.setX(2, 1);
                
        a0.E(atom0.getOrientation().getDirection());
        a1.E(atom1.getOrientation().getDirection());
        
        dr.E(0);
        dr.setX(0, rCM);        
        double cth1 = a0.dot(ex);
        if (cth1 > 1.0) cth1 = 1.0;
        if (cth1 < -1.0) cth1 = -1.0;
        th1 = Math.acos(cth1);
        double cth2 = a1.dot(ex);
        if (cth2 > 1.0) cth2 = 1.0;
        if (cth2 < -1.0) cth2 = -1.0;
        th2 = Math.acos(cth2);
        Vector n0 = space.makeVector();
        Vector n1 = space.makeVector();
        n0.E(a0);
        n0.PEa1Tv1(-cth1, ex);
        if (n0.isZero())  n0.E(ey);
        n0.normalize();

        n1.E(a1);        
        n1.PEa1Tv1(-cth2, ex);
        if (n1.isZero())  n1.E(ey);
        n1.normalize();        
        
        if (n0.isNaN() || n1.isNaN()) throw new RuntimeException("oops");

        double cphi = n0.dot(n1);
        if (cphi > 1.0) cphi = 1.0;
        if (cphi < -1.0) cphi = -1.0;
        phi = Math.acos(cphi);        
        
        if (rCM <= rMin) return Double.POSITIVE_INFINITY;
        double u = abicipeso4(rCM, th1, th2, phi);        
        return u;
    }
    
