        c8 += parab[26][itypea[ia-1]-1][itypeb[ib-1]-1]*(s2*s5);

        c10 += parab[12][itypea[ia-1]-1][itypeb[ib-1]-1]*(s3+s6);
        c10 += parab[15][itypea[ia-1]-1][itypeb[ib-1]-1]*(s1+s4);
        c10 += parab[18][itypea[ia-1]-1][itypeb[ib-1]-1]*(s2+s5);
