                    values[52] =-(s1*s2-s4*s5)*val0;
                    values[53] =-(s1*s2-s4*s5)*val1;
                    values[54] =-(s1*s2-s4*s4)*val2;
                    values[55] =-(s1*s2-s4*s4)*val3;

